import React from "react";

import Slider from "react-animated-slider";
import "react-animated-slider/build/horizontal.css";

const IndexSlider = () => {
  const slides = [
    {
      title: "En psychomotricité",
      description: "on vit, on ressent, on expérimente, on exprime des sensations et des émotions",
      backgroundImage: "/main/psychomotricite_slider.jpg",
      link
    },
    {
      title: "le yoga",
      description: "une pratique douce accessible pour tous",
    },
    {
      title: "la sophrologie",
      description:
        "Une démarche positive pour retrouver Equilibre et Harmonie.",
    },
  ];
  return (
    <Slider infinite={true}>
      {slides.map((slide, index) => (
        <div key={index} style={slide.backgroundImage ? {background : `url(${slide.backgroundImage})`, backgroundSize:"cover"} : null}>
          <h2>{slide.title}</h2>
          <p>{slide.description}</p>
        </div>
      ))}
    </Slider>
  );
};

export default IndexSlider;
