import Image from "next/image"
import "../static/css/goTop.css"

import {gsap} from "gsap"
import {useEffect,useRef} from "react"

export default function GoTop(){

    const handleClick = () =>{
        window.scrollTo({top:0,behavior:"smooth"})
    }

    const button = useRef()
    useEffect(() => {
        const tl = gsap.timeline()
        .to(button,{
            y:"-=5px",
            duration:.3
        })
    },[])

    return(
        <div ref={button} className="scrollTopContainer" onClick={handleClick}>
            <img src="/main/scrollTop.svg" alt="Retourner en haut de la page"/>
        </div>
    )
}
